settings.liquid:
{%- form 'customer', id: 'warranty-registration', class: 'form' -%}
      {%- if form.posted_successfully? -%}
        {%- assign success_message = "Thank you for your registration. We've got you covered for the limited 1 year warranty now." -%}
        {%- render 'banner', content: success_message, status: 'success' -%}
      {%- else -%}
        {%- if form.errors -%}
          {%- capture error_message -%}{{ form.errors.translated_fields['email'] }} {{ form.errors.messages['email'] }}{%- endcapture -%}
          {%- render 'banner', content: error_message, status: 'error' -%}
        {%- endif -%}

        <input type="hidden" name="contact[tags]" value="Warranty Registrant">

        <div class="fieldset">
          {%- render 'input', name: 'contact[first_name]', label: "First name", type: 'text', required: true -%}
          {%- render 'input', name: 'contact[last_name]', label: "Last name", type: 'text', required: true -%}
          {%- capture label -%}{{ 'general.newsletter.email' | t }}{%- endcapture -%}
          {%- render 'input', name: 'contact[email]', label: label, type: 'email', required: true, autocomplete: 'email', enterkeyhint: 'send' -%}
          {%- render 'input', name: 'contact[Note][Order Number]', label: 'Order number #', type: 'text', required: true, autocomplete: 'email', enterkeyhint: 'send' -%}

        </div>
        {%- render 'button', type: 'submit', content: 'Submit Warranty Registration', size: 'xl', background: section.settings.button_background, text_color: section.settings.button_text_color -%}
      {%- endif -%}
    {%- endform -%}
