settings.liquid:
{% if product.metafields.coffe_attributes.taste != blank %}
<p class="tp-body bold" style="margin-top:0px;margin-bottom:0px">{{ product.metafields.coffe_attributes.taste }}</p>
{% endif %}

{% if product.available == false %}
<p class="tp-display-2" style="margin-bottom:5px">Don't miss out on restocks</p>
<p class="tp-body" style="margin-bottom:5px">Be the first to know when <strong>{{product.title}}</strong> is back in stock.</p>
    {%- form 'customer', id: 'newsletter-form-restock-machine', class: 'form' -%}
      {%- if form.posted_successfully? -%}
        {%- assign success_message = 'Success! Check your email to confirm you want to be notified when we restock' -%}
        {%- render 'banner', content: success_message, status: 'success' -%}
      {%- else -%}
        {%- if form.errors -%}
          {%- capture error_message -%}{{ form.errors.translated_fields['email'] }} {{ form.errors.messages['email'] }}{%- endcapture -%}
          {%- render 'banner', content: error_message, status: 'error' -%}
        {%- endif -%}

        <input type="hidden" name="contact[tags]" value="newsletter, restock_coffee">

        <div class="fieldset-with-submit">
          {%- capture label -%}{{ 'general.newsletter.email' | t }}{%- endcapture -%}
          {%- render 'input', name: 'contact[email]', label: label, type: 'email', required: true, autocomplete: 'email', enterkeyhint: 'send' -%}
          {%- render 'button', type: 'submit', content: 'Notify Me', size: 'xl', background: section.settings.button_background, text_color: section.settings.button_text_color -%}
        </div>
      {%- endif -%}
    {%- endform -%}
{% endif %}
