settings.liquid:
{% if product.metafields.coffee_attributes.intensity != blank %}
<div class="attribute-line">
<p class="tp-body bold attribute-title">Intensity:</p>
<div class="bohnen-icons">
<img src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_high.svg?v=1674723060" width="15px" class="bohne">
<img {% if product.metafields.coffee_attributes.intensity.value >= 2 %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_high.svg?v=1674723060" {% else  %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_low.svg?v=1674723060" {% endif %} width="15px" class="bohne">
<img {% if product.metafields.coffee_attributes.intensity.value >= 3 %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_high.svg?v=1674723060" {% else  %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_low.svg?v=1674723060" {% endif %} width="15px" class="bohne">
<img {% if product.metafields.coffee_attributes.intensity.value >= 4 %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_high.svg?v=1674723060" {% else  %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_low.svg?v=1674723060" {% endif %} width="15px" class="bohne">
<img {% if product.metafields.coffee_attributes.intensity.value >= 5 %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_high.svg?v=1674723060" {% else  %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_low.svg?v=1674723060" {% endif %} width="15px" class="bohne">
<img {% if product.metafields.coffee_attributes.intensity.value >= 6 %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_high.svg?v=1674723060" {% else  %} src="https://cdn.shopify.com/s/files/1/0618/9386/9774/files/intensity_low.svg?v=1674723060" {% endif %} width="15px" class="bohne">
</div>
</div>
{% endif %}

<style>
.attribute-line {
display: flex;
margin-bottom:10px;
align-items: center;
}
.attribute-title {
margin-right: 5px;
}
.bohnen-icons {
display:flex;
}
.bohne {
margin: 0 2px;
}
</style>

{% if product.available == false %}
<p class="tp-display-2" style="margin-bottom:5px">Don't miss out on restocks</p>
<p class="tp-body" style="margin-bottom:5px">Be the first to know when <strong>{{product.title}}</strong> is back in stock.</p>
    {%- form 'customer', id: 'newsletter-form-restock-machine', class: 'form' -%}
      {%- if form.posted_successfully? -%}
        {%- assign success_message = 'Success! Check your email to confirm you want to be notified when we restock' -%}
        {%- render 'banner', content: success_message, status: 'success' -%}
      {%- else -%}
        {%- if form.errors -%}
          {%- capture error_message -%}{{ form.errors.translated_fields['email'] }} {{ form.errors.messages['email'] }}{%- endcapture -%}
          {%- render 'banner', content: error_message, status: 'error' -%}
        {%- endif -%}

        <input type="hidden" name="contact[tags]" value="newsletter, restock_coffee">

        <div class="fieldset-with-submit">
          {%- capture label -%}{{ 'general.newsletter.email' | t }}{%- endcapture -%}
          {%- render 'input', name: 'contact[email]', label: label, type: 'email', required: true, autocomplete: 'email', enterkeyhint: 'send' -%}
          {%- render 'button', type: 'submit', content: 'Notify Me', size: 'xl', background: section.settings.button_background, text_color: section.settings.button_text_color -%}
        </div>
      {%- endif -%}
    {%- endform -%}
{% endif %}
